CREATE TABLE PASSENGER(first_name VARCHAR(35) NOT NULL, last_name VARCHAR(35) NOT NULL, address VARCHAR(50) NOT NULL, city VARCHAR(20) NOT NULL, county VARCHAR(20) NOT NULL, phone VARCHAR(12) NOT NULL, SSN CHAR(9) NOT NULL, bdate DATE NOT NULL, PRIMARY KEY (SSN));
CREATE TABLE TRAIN(Train_Number INT NOT NULL, Train_Name VARCHAR(20) NOT NULL, Premium_Fair INT NOT NULL, General_Fair INT NOT NULL, Source_Station VARCHAR(20) NOT NULL, Destination_Station VARCHAR(20) NOT NULL, Available_on1 VARCHAR(10),Available_on2 VARCHAR(10),Available_on3 VARCHAR(10), PRIMARY KEY(Train_Number, Train_Name));
CREATE TABLE TRAIN_STATUS(TrainDate DATE NOT NULL, TrainName VARCHAR(20) NOT NULL, PremiumSeatsAvailable INT, GenSeatsAvailable INT, PremiumSeatsOccupied INT, GenSeatsOccupied INT, PRIMARY KEY(TrainName), FOREIGN KEY (TrainName) REFERENCES Train(Train_Name));
CREATE TABLE BOOKED(Passanger_ssn CHAR(9) NOT NULL, Train_Number INT  NOT NULL, Ticket_type VARCHAR(7) NOT NULL, Staus VARCHAR(6) NOT NULL, FOREIGN KEY(Passanger_ssn) REFERENCES PASSENGER(SSN), FOREIGN KEY (Train_Number) REFERENCES Train(Train_Number));
